INSERT INTO `ai_gpt_chat_message` VALUES (80292352014772237, 79656945963020288, '4a00f89d-cda4-4310-8a89-54bc381f6133', '', 'ChatGPT', 'moonshot-v1-8k', '// debugger\nlet controller = new AbortController();\n// const openLongReply = import.meta.env.VITE_GLOB_OPEN_LONG_REPLY === \'true\'\nconst dialog = useDialog();\n// const ms = useMessage()\n\nconst appStore = useAppStore();\nconst chatStore = useChatStore();\nconst { isMobile } = useBasicLayout();\nconst { scrollRef, scrollToBottom, scrollToBottomIfAtBottom } = useScroll();\nconst { addChat, updateChat, updateChatLoading, getChatByIndex } = useChat();\n\nconst dataSources = ref<Chat.Chat[]>([]);\nconst loading = ref<boolean>(false);\nconst inputRef = ref<Ref | null>(null);\nconst ideaRef = ref<Ref | null>(null);\n// 提示词\nconst prompt = ref<string>(\'\');\n// 是否展示提示词\nconst showPrompt = ref<boolean>(true);\n// 计算灵感大全-提示词高度\nconst computedHeight = ref<string>(\'\');\n\n// 监听选中信息刷新对话内容\nwatch(\n  () => chatStore.active,\n  () => {\n    chatStore.getChatByChatNumber(chatStore.active).then((res: any[]) => {\n      dataSources.value = res;\n      scrollToBottom();\n    });\n  }\n);\n\n// 更新聊天数据loading状态\ndataSources.value.forEach((item, index) => {\n  if (item.loading) {\n    updateChatLoading(chatStore.active, index, { loading: false });\n  }\n});\n\n// 输入框提示\nconst placeholder = computed(() => {\n  if (isMobile.value) return $t(\'chat.placeholderMobile\');\n  return $t(\'ai.placeholder\');\n});\n\n// 发送按钮是否禁用\nconst buttonDisabled = computed(() => {\n  return loading.value || !prompt.value || prompt.value.trim() === \'\';\n});\n\n// 内容样式\nconst indexClass = computed(() => {\n  let classes = [\'w-[800px]\'];\n  if (isMobile.value) classes = [\'w-full\', \'overflow-hidden\'];\n  return classes;\n});\n\n// 输入框高度监听\nconst handleInput = () => {\n  inputRef.value.style.height = \'auto\';\n  inputRef.value.style.height = ${inputRef.value.scrollHeight - 13}px;\n};\n\n// 监听浏览器高度自动\nwindow.addEventListener(\'resize\', () => {\n  computedHeight.value = ${window.innerHeight - ideaRef.value.clientHeight - 2}px;\n});\n\nonMounted(async () => {\n  if (prompt.value) {\n    const message = prompt.value;\n    const chatNumber: string = chatStore.active;\n    const messageData: Chat.MessageRequest = {\n      chatNumber,\n      model: chatStore.model,\n      modelVersion: \'moonshot-v1-8k\',\n      systemPrompt: message,\n      prompt: message\n    };\n    // 发送内容\n    const messageRes = await fetchChatMessageAPI(messageData);\n    // 流式响应\n    onConversation(chatNumber, messageRes.data);\n  }\n  chatStore.getChatByChatNumber(chatStore.active).then((res: any[]) => {\n    dataSources.value = res;\n    handleSubmit();\n  });\n  if (inputRef.value && !isMobile.value) {\n    inputRef.value?.focus();\n  }\n  computedHeight.value = ${window.innerHeight - ideaRef.value.clientHeight - 184}px;\n  inputRef.value.addEventListener(\'input\', handleInput);\n  assistantType();\n  scrollToBottom();\n});\n\nonUnmounted(() => {\n  if (loading.value) {\n    controller.abort();\n  }\n});\n\n// 助手分类\ninterface AssistantType {\n  id: number;\n  name: string;\n  icon: string;\n}\n\nconst assistantTypeId = ref<number>(0);\nconst assistantTypeValue = ref<Array<AssistantType>>([]);\nconst fixedAssistantTypeValue = ref<Array<AssistantType>>([]);\n\n// 获取助手分类\nfunction assistantType() {\n  listAssistantType<Array<AssistantType>>().then((res: { data: { id: number; name: string; icon: string }[] }) => {\n    const assistantType: AssistantType = {\n      id: 0,\n      name: \'全部\',\n      icon: \'ri:message-line\'\n    };\n    assistantTypeValue.value = res.data;\n    assistantTypeValue.value.splice(0, 0, assistantType);\n    fixedAssistantTypeValue.value = assistantTypeValue.value.slice(0, 5);\n  });\n}\n\n// 助手分类选择\nfunction chooseType(typeId: number) {\n  assistantTypeId.value = typeId;\n}\n\n// 助手分类是否选中\nfunction isActive(typeId: number) {\n  return assistantTypeId.value === typeId;\n}\n\n// 选择右侧固定分类\nfunction chooseFixedType(typeId: number | null) {\n  if (typeId) {\n    assistantTypeId.value = typeId;\n  }\n  showPrompt.value = true;\n}\n\n// 触发选择助手\nfunction handAssistant(assistantId: number) {\n  chatStore.setActive(\'\');\n  handleSubmit(assistantId);\n}\n\n// 编辑助手\nfunction editAssistant(systemPrompt: string) {\n  prompt.value = systemPrompt;\n  setTimeout(() => {\n    inputRef.value.style.height = \'auto\';\n    inputRef.value.style.height = ${inputRef.value.scrollHeight}px;\n  }, 50);\n}\n\n// 发送消息\nfunction handleEnter(event: KeyboardEvent) {\n  if (!isMobile.value) {\n    if (event.key === \'Enter\' && !event.shiftKey) {\n      event.preventDefault();\n      handleSubmit();\n    }\n  } else if (event.key === \'Enter\' && event.ctrlKey) {\n    event.preventDefault();\n    handleSubmit();\n  }\n}\n\n// 停止输出\nfunction handleStop() {\n  if (loading.value) {\n    controller.abort();\n    loading.value = false;\n  }\n}\nconst uploadRef = ref();\nconst fileId = ref();\nconst fileList = ref([]); // 存储文件信息\nconst selectedFile = ref<File | null>(null); // 真实的文件数据\nconst uploadedData = ref(null);\n// 处理文件变化\nconst handleFileChange = async (options: { fileList: any[] }) => {\n  fileList.value = options.fileList.map(file => {\n    const isImage = /\\.(jpg|jpeg|png)$/i.test(file.name);\n    return {\n      name: file.name,\n      url: isImage ? URL.createObjectURL(file.file) : \'\',\n      isImage,\n      size: file.file.size\n    };\n  });\n\n  if (options.fileList.length > 0) {\n    selectedFile.value = options.fileList[0].file;\n    prompt.value = \'\';\n  } else {\n    selectedFile.value = null;\n  }\n\n  if (!selectedFile.value) {\n    console.error(\'No file selected\');\n    return;\n  }\n\n  const message = prompt.value;\n  const chatNumber: string = chatStore.active;\n  const model: string = chatStore.model;\n  const formData = new FormData();\n  formData.append(\'file\', selectedFile.value);\n  formData.append(\'model\', model);\n  formData.append(\'chatNumber\', chatNumber);\n  const chatData: Chat.ChatRequest = {\n    systemPrompt: message,\n    prompt: message\n  };\n\n  // 如果 chatNumber 不存在，则创建新的\n  if (!chatNumber) {\n    const chatRes = await fetchChatAPI(chatData);\n    const chatNumber = chatRes.data.chatNumber;\n    chatStore.setActive(chatNumber);\n    await nextTick();\n  }\n\n  try {\n    const response = await request({\n      url: \'/ai/chat/upload\',\n      method: \'POST\',\n      data: formData,\n      headers: {\n        \'Content-Type\': \'multipart/form-data\'\n      }\n    });\n\n    fileId.value = response.data.fileId;\n    const messageData: Chat.MessageRequest = {\n      chatNumber,\n      model: chatStore.model,\n      modelVersion: \'moonshot-v1-8k\',\n      systemPrompt: message,\n      prompt: message\n    };\n\n    // 发送内容\n    const messageRes = await fetchChatMessageAPI(messageData);\n    chatStore.setPendingMessage(messageRes.data);\n    // 流式响应\n    // onConversation(chatNumber, messageRes.data);\n    // handleSubmit();\n  } catch (error) {\n    console.error(\'Upload failed:\', error);\n  }\n};\n\n// 发送\nasync function handleSubmit(assistantId?: number) {\n  let message = prompt.value;\n  let chatNumber: string = chatStore.active;\n  if (loading.value) return;\n\n  // 没有选择助手的时候判断文字\n  if (!assistantId) {\n    if (!message || message.trim() === \'\') return;\n  }\n  const chatData: Chat.ChatRequest = {\n    assistantId,\n    systemPrompt: message,\n    prompt: message\n  };\n\n  // 创建会话\n  if (!chatNumber) {\n    const chatRes = await fetchChatAPI(chatData);\n    chatNumber = chatRes.data.chatNumber;\n    message = chatRes.data.prompt;\n    if (assistantId) {\n      prompt.value = chatRes.data.prompt;\n    }\n    chatStore.setActive(chatNumber);\n    await nextTick();\n  }\n  const messageData: Chat.MessageRequest = {\n    chatNumber,\n    model: chatStore.model,\n    modelVersion: \'moonshot-v1-8k\',\n    systemPrompt: message,\n    prompt: message\n  };\n\n  // 发送内容\n  const messageRes = await fetchChatMessageAPI(messageData);\n\n  // 流式响应\n  onConversation(chatNumber, messageRes.data);\n}\n\n/** sse对话 */\nasync function onConversation(chatNumber: string, conversationId: string) {\n  const message = prompt.value;\n  controller = new AbortController();\n\n  addChat(chatNumber, {\n    conversationId,\n    dateTime: new Date().toLocaleString(),\n    contentType: \'text\',\n    text: message,\n    model: chatStore.model,\n    inversion: true,\n    error: false\n  });\n\n  scrollToBottom();\n\n  loading.value = true;\n  prompt.value = \'\';\n\n  setTimeout(() => {\n    inputRef.value.style.height = \'auto\';\n    inputRef.value.style.height = ${inputRef.value.scrollHeight - 13}px;\n  }, 50);\n\n  addChat(chatNumber, {\n    parentMessageId: conversationId,\n    dateTime: new Date().toLocaleString(),\n    contentType: \'text\',\n    text: \'\',\n    images: [],\n    model: chatStore.model,\n    loading: true,\n    inversion: false,\n    error: false\n  });\n\n  scrollToBottom();\n  try {\n    const lastText = \'\';\n    const fetchChatAPIOnce = async () => {\n      await fetchChatAPIProcess<Chat.ConversationResponse>({\n        conversationId,\n        signal: controller.signal,\n        // 先调用oploadFile 接口，拿到fileId，发送消息的时候传入\n        fileIds: [fileId.value],\n        onDownloadProgress: ({ event }) => {\n          const xhr = event.target;\n          const { responseText } = xhr;\n          const lastIndex = responseText.lastIndexOf(\'\\n\', responseText.length - 2);\n          let chunk = responseText;\n          if (lastIndex !== -1) {\n            chunk = responseText.substring(lastIndex);\n          }\n          try {\n            const data = JSON.parse(chunk);\n            updateChat(chatNumber, dataSources.value.length - 1, {\n              conversationId: data.conversationId,\n              parentMessageId: conversationId,\n              dateTime: new Date().toLocaleString(),\n              contentType: data.contentType,\n              text: data.contentType === \'text\' ? lastText + (data.content ?? \'\') : \'\',\n              images:\n                data.contentType === \'image\' && data.content\n                  ? data.content.map((d: { b64Image: any }) => {\n                      return d.b64Image;\n                    })\n                  : [],\n              model: chatStore.model,\n              inversion: false,\n              error: false,\n              loading: true\n            });\n            nextTick(() => {\n              scrollToBottom();\n              scrollToBottomIfAtBottom();\n            });\n          } catch (error) {\n            console.info(\'回复错误:\', error);\n          }\n        }\n      });\n      updateChatLoading(chatNumber, dataSources.value.length - 1, {\n        loading: false\n      });\n    };\n    await fetchChatAPIOnce();\n  } catch (error: any) {\n    const errorMessage = error?.message ?? $t(\'common.wrong\');\n    if (error?.message === \'canceled\') {\n      updateChatLoading(chatNumber, dataSources.value.length - 1, {\n        loading: false\n      });\n      scrollToBottomIfAtBottom();\n      return;\n    }\n\n    const currentChat = getChatByIndex(chatNumber, dataSources.value.length - 1);\n\n    if (currentChat?.text && currentChat.text !== \'\') {\n      updateChatLoading(chatNumber, dataSources.value.length - 1, {\n        text: ${currentChat.text}\\n[${errorMessage}],\n        error: false,\n        loading: false\n      });\n      return;\n    }\n\n    updateChat(chatNumber, dataSources.value.length - 1, {\n      dateTime: new Date().toLocaleString(),\n      contentType: \'text\',\n      text: errorMessage,\n      inversion: false,\n      error: true,\n      loading: false\n    });\n    scrollToBottomIfAtBottom();\n  } finally {\n    loading.value = false;\n  }\n}\n\n/** 重新回复 */\nasync function onRegenerate(messageId?: string | null) {\n  if (!messageId) return;\n  fetchChatMessageById(messageId).then((res: { data: { content: string } }) => {\n    prompt.value = res.data.content;\n    handleSubmit();\n  });\n}\n\n// 删除消息\nfunction handleDelete(index: number, messageId?: string | null) {\n  if (loading.value) return;\n  if (!messageId) return;\n  dialog.warning({\n    title: $t(\'chat.deleteMessage\'),\n    content: $t(\'chat.deleteMessageConfirm\'),\n    positiveText: $t(\'common.yes\'),\n    negativeText: $t(\'common.no\'),\n    onPositiveClick: () => {\n      chatStore.deleteChatByUuid(chatStore.active, index);\n      chatStore.deleteChatByUuid(chatStore.active, index - 1);\n      deleteChatMessageById(messageId);\n    }\n  });\n}\nconst formatFileSize = size => {\n  if (size < 1024) return ${size} B;\n  if (size < 1024 * 1024) return ${(size / 1024).toFixed(1)} KB;\n  return ${(size / (1024 * 1024)).toFixed(1)} MB;\n};\nconst deleteFile = file => {\n  const index = fileList.value.findIndex(f => f.name === file.name);\n  if (index !== -1) {\n    fileList.value.splice(index, 1);\n  }\n};\nconst deleteButtonStyle = {\n  position: \'absolute\',\n  top: \'0px\',\n  right: \'-14px\',\n  height: \'18px\'\n};\n\n// 跳转页面\nfunction jumpToXieyi(type: number) {\n  const { href } = router.resolve({\n    path: \'/agreement\',\n    query: { type }\n  });\n  // 打开新窗口\n  window.open(href);\n}\n</script>\n\n<template>\n  <div class=\"h-full w-full flex bg-slate-50 dark:bg-[#18181c]\" style=\"padding: 0 16px\">\n    <Layout />\n    <div class=\"m-auto h-full flex flex-col\" :class=\"indexClass\">\n      <main class=\"mb-8 w-full flex-1 overflow-hidden pt-4\">\n        <div id=\"scrollRef\" ref=\"scrollRef\" class=\"no-scrollbar h-full overflow-hidden overflow-y-auto\">\n          <div id=\"image-wrapper\" class=\"max-w-screen-xl dark:bg-[#18181c]\">\n            <template v-if=\"!dataSources.length\">\n              <Top @hand-assistant=\"handAssistant\" />\n            </template>\n            <template v-else>\n              <div>\n                <Message\n                  v-for=\"(item, index) of dataSources\"\n                  :key=\"index\"\n                  :date-time=\"item.dateTime\"\n                  :content-type=\"item.contentType\"\n                  :text=\"item.text\"\n                  :images=\"item.images\"\n                  :model=\"item.model\"\n                  :inversion=\"item.inversion\"\n                  :error=\"item.error\"\n                  :loading=\"item.loading\"\n                  @regenerate=\"onRegenerate(item.parentMessageId)\"\n                  @delete=\"handleDelete(index, item.conversationId)\"\n                />\n                <div class=\"h-10\"></div>\n                <div class=\"sticky bottom-0 left-0 flex justify-center\">\n                  <NButton v-if=\"loading\" @click=\"handleStop\">\n                    <template #icon>\n                      <SvgIcon icon=\"ri:stop-circle-line\" />\n                    </template>\n                    {{ $t(\'ai.stopResponding\') }}\n                  </NButton>\n                </div>\n              </div>\n            </template>\n          </div>\n        </div>\n      </main>\n      <footer class=\"w-full\" style=\"position: relative\">\n        <div\n          class=\"mb-2 max-h-[282px] max-w-screen-xl flex justify-between border border-slate-300 rounded-xl bg-white p-4 dark:border-none dark:bg-[#24272e]\"\n          style=\"display: flex; flex-direction: column; width: auto\"\n        >\n          <div class=\"w-full\">\n            <NCarousel\n              v-if=\"fileList.length > 0\"\n              show-dots=\"never\"\n              :slides-per-view=\"4\"\n              :draggable=\"true\"\n              show-arrows=\"true\"\n              class=\"w-full\"\n              style=\"height: 110px\"\n            >\n              <template v-for=\"file in fileList\" :key=\"file.name\">\n                <div\n                  v-if=\"file.isImage\"\n                  class=\"file-item flex flex-row items-center border rounded-lg bg-gray-100 p-1 dark:bg-[#24272e]\"\n                  @mouseover=\"file.showDelete = true\"\n                  @mouseleave=\"file.showDelete = false\"\n                >\n                  <NImage :src=\"file.url\" alt=\"上传图片\" class=\"mr-[5%] h-12 w-auto rounded-md object-cover\" />\n                  <div style=\"display: flex; flex-direction: column; align-items: center\">\n                    <span\n                      class=\"mt-1 w-20 overflow-hidden text-ellipsis whitespace-nowrap break-all text-center text-xs\"\n                    >\n                      {{ file.name }}\n                    </span>\n                    <span class=\"text-xs text-gray-500\">{{ formatFileSize(file.size) }}</span>\n                  </div>\n                  <SvgIcon\n                    v-if=\"file.showDelete\"\n                    icon=\"material-symbols:delete-outline\"\n                    class=\"mr-[5%] cursor-pointer text-3xl text-gray-600\"\n                    :style=\"deleteButtonStyle\"\n                    @click=\"deleteFile(file)\"\n                  />\n                </div>\n                <div\n                  v-else\n                  class=\"file-item flex flex-row items-center border rounded-lg bg-gray-100 p-1 dark:bg-[#24272e]\"\n                  @mouseover=\"file.showDelete = true\"\n                  @mouseleave=\"file.showDelete = false\"\n                >\n                  <SvgIcon icon=\"mdi:file-document-outline\" class=\"mr-[5%] text-3xl text-gray-600\" />\n                  <div style=\"display: flex; flex-direction: column; align-items: center\">\n                    <span class=\"mt-1 w-20 break-all text-center text-xs\">{{ file.name }}</span>\n                    <span class=\"text-xs text-gray-500\">{{ formatFileSize(file.size) }}</span>\n                  </div>\n                  <SvgIcon\n                    v-if=\"file.showDelete\"\n                    icon=\"material-symbols:delete-outline\"\n                    class=\"mr-[5%] text-3xl text-gray-600\"\n                    :style=\"deleteButtonStyle\"\n    