mapper.delete:
delete from def_login_log


        AND create_time  <=  #{clearBeforeTime}


        AND id NOT in

            #{id}

mapper.select:
SELECT COUNT(*) FROM (
SELECT employee_id AS uid, COUNT(*) AS total
FROM def_login_log

    AND status = '01'

        AND login_date  >=  #{start}


        AND login_date  <=  #{end}


GROUP BY employee_id
HAVING COUNT(*)  >=  #{minTimes}
) t

SELECT employee_id AS uid, COUNT(*) AS total
FROM def_login_log

    AND status = '01'

        AND login_date  >=  #{start}


        AND login_date  <=  #{end}


GROUP BY employee_id
ORDER BY total DESC

    LIMIT #{limit}
